$query = new Zend_Search_Lucene_Search_Query_Boolean();\nforeach ($words as $word) {\n    $wildcard1 = new Zend_Search_Lucene_Index_Term($word . '*', $fieldname1);\n    $wildcardQuery1 = new Zend_Search_Lucene_Search_Query_Wildcard($wildcard1);\n\n    $wildcard2 = new Zend_Search_Lucene_Index_Term($word . '*', $fieldname2);\n    $wildcardQuery2 = new Zend_Search_Lucene_Search_Query_Wildcard($wildcard2);\n\n    // Combine the two wildcard queries with OR\n    $subquery = new Zend_Search_Lucene_Search_Query_Boolean();\n    $subquery->addSubquery($wildcardQuery1, true); // required clause\n    $subquery->addSubquery($wildcardQuery2, true); // required clause\n\n    // Add the combined subquery to the main query with OR\n    $query->addSubquery($subquery, false);\n}\n$hits = $index->find($query);