// Example of a simple MySQL query\n$query = "SELECT name FROM categories";\n$result = mysqli_query($connection, $query);\n\n$categories = [];\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $categories[] = htmlspecialchars($row['name'], ENT_QUOTES, 'UTF-8');\n}\n\n// Assuming you have a template to fill with these categories\n$html = file_get_contents('file.txt');\necho str_replace('{categories}', implode(', ', $categories), $html);