function getValueByString($array, $string) {\n    // Remove any initial or trailing brackets and split by ']['\n    $keys = preg_split('/\]\s*\[/', trim($string, '[]'));\n    \n    // Navigate through the array using the keys\n    foreach ($keys as $key) {\n        if (isset($array[$key])) {\n            $array = $array[$key];\n        } else {\n            return null; // or handle the error as needed\n        }\n    }\n    \n    return $array;\n}\n\n// Example usage:\n$array = [\n    'product' => [\n        [0, [1, 2]],\n        ['a', ['b', 'c']]\n    ]\n];\n\n$string = 'product[0][1][0]';\n$value = getValueByString($array, $string);\necho $value; // Outputs: 2