<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Check if availability array is set and not empty\n    if (!empty($_POST['availability'])) {\n        $availabilities = $_POST['availability'];\n        \n        // Process each selected day\n        foreach ($availabilities as $day) {\n            echo htmlspecialchars($day) . "<br>";\n            // Here you can add your logic to save or process the selected days\n        }\n    } else {\n        echo "No days were selected.";\n    }\n}\n?>