<?php\nob_start();\nphpinfo();\n$info = ob_get_clean();\n\npreg_match_all('#(?:<tr><td[^>]+>(.*?)</td>' .\n             '<td[^>]+>(.*?)</td></tr>)#is', $info, $matches);\n\n$iniArray = [];\nforeach ($matches[1] as $i => $key) {\n    if (strpos($matches[2][$i], '</a>') > 0) {\n        continue; // Skip the settings with links\n    }\n    $key = trim(str_replace('</td>', '', strip_tags($matches[1][$i])));\n    $value = trim(str_replace('<span style="color: #0073ea;">.*?</span>', '', stripcslashes(strip_tags($matches[2][$i]))));\n    if ($key && !empty($value)) {\n        $iniArray[$key] = $value;\n    }\n}\n\n// Convert the array to ini format\n$iniContent = '';\nforeach ($iniArray as $key => $value) {\n    // Handle special cases, e.g., memory_limit, display_errors\n    if (is_numeric($value) || in_array(strtolower($value), ['on', 'off'])) {\n        $iniContent .= "$key = $value\n";\n    } else {\n        $iniContent .= "$key = \"$value\"\n";\n    }\n}\n\n// Write to php.ini (make sure you have write permissions or run this as root/sudo)\nfile_put_contents('extracted_php.ini', $iniContent);\n?>