<?php\nsession_set_cookie_params([\n    'lifetime' => 0, // Lifetime of the cookie, default is until the browser is closed\n    'path'     => '/',\n    'domain'   => '.example.com', // Note the leading dot to allow subdomains\n    'secure'   => true,           // Only send the cookie over HTTPS\n    'httponly' => true            // Prevent access from JavaScript\n]);\nsession_start();\n\n// Set a session variable\n$_SESSION['SessionID'] = uniqid('sess_');\n?>