<?php\nheader('Content-Type: image/png');\n$num = 42; // Your dynamic number here\n$image = imagecreatetruecolor(100, 50);\n$bgColor = imagecolorallocate($image, 255, 255, 255); // White background\n$textColor = imagecolorallocate($image, 0, 0, 0); // Black text\nimagefilledrectangle($image, 0, 0, 100, 50, $bgColor);\nimagettftext(\n    $image,\n    24,    // Font size\n    0,     // Rotation angle in degrees\n    5,     // X co-ordinate of the lower left corner\n    35,    // Y co-ordinate of the lower left corner\n    $textColor,\n    __DIR__ . '/path/to/font.ttf', // Path to TTF Font\n    $num\n);\nimagepng($image);\nimagedestroy($image);\n?>