$xml = file_get_contents('http://api.flickr.com/services/feeds/geo/?format=rss_200');\n$doc = new DOMDocument();\n@$doc->loadXML($xml); // Suppress warnings for XML errors\n\n// Create a namespace map to use media:content correctly\n$xpath = new DOMXPath($doc);\n$xpath->registerNamespace('media', 'http://search.yahoo.com/mrss/');\n\n$query = '//media:content/@url';\n$nodes = $xpath->query($query);\n\nforeach ($nodes as $node) {\n    echo $node->nodeValue . "\n"; // Access the value of the attribute\n}