<?php\n// Assuming you have a connection already\n$conn = new mysqli("host", "username", "password", "database");\n\n// Your data\n$id = 1;\n$titles = ["english title", "nederlandse titel"];\n\n// Serialize the array\n$serializedTitles = serialize($titles);\n\n// Insert into database\n$sql = "INSERT INTO your_table (ID, TITLE) VALUES (?, ?)";\n$stmt = $conn->prepare($sql);\n$stmt->bind_param("is", $id, $serializedTitles);\n$stmt->execute();\n\n// Fetch and unserialize data\n$result = $conn->query("SELECT * FROM your_table WHERE ID = 1");\n$row = $result->fetch_assoc();\n$retrievedTitles = unserialize($row['TITLE']);\n\nprint_r($retrievedTitles); // Outputs: Array ( [0] => english title [1] => nederlandse titel )\n\n$stmt->close();\n$conn->close();\n?>