$filesQuery = "SELECT FILENAME FROM FILES WHERE ARTICLE_NO = ?"; \n\nif ($getFiles = $con->prepare($filesQuery)) {\n    $getFiles->bind_param("i", $pk); // Changed to "i" for integer binding\n    $getFiles->execute();\n    $getFiles->bind_result($FILENAME);\n    $files = array();\n\n    while ($getFiles->fetch()) {\n        $filenames = array(\n            'FILENAME' => $FILENAME,\n        );\n        $files[] = $filenames;\n    }\n}\n\nif (!empty($files)) { // Check if the files array is not empty\n    foreach ($files as $file) {\n        echo '<p><a href="./files/'. htmlspecialchars($file['FILENAME']) .'">'. htmlspecialchars($file['FILENAME']) .'</a></p>'; \n    }\n} else {\n    echo "No files found.";\n}