class User_Model extends CI_Model {\n    // Your user model methods here\n}\n\nclass Relation_Model extends CI_Model {\n    protected $user_model;\n\n    public function __construct(User_Model $user_model) {\n        $this->user_model = $user_model;\n    }\n\n    public function getRelation($userID) {\n        // Use $this->user_model to access User_Model methods\n        return "Relation for user ID: " . $userID;\n    }\n}\n\nclass Reminder_Model extends CI_Model {\n    protected $user_model;\n\n    public function __construct(User_Model $user_model) {\n        $this->user_model = $user_model;\n    }\n\n    public function useGetRelationFromRelationModel($userID, Relation_Model $relation_model) {\n        return $relation_model->getRelation($userID);\n    }\n}\n\n// In your controller or another part of the application where you instantiate models\n$user_model_1 = new User_Model();\n$user_model_2 = new User_Model();\n\n$relation_model = new Relation_Model($user_model_1);\n$reminder_model = new Reminder_Model($user_model_2);\n\necho $reminder_model->useGetRelationFromRelationModel(5, $relation_model); // Outputs: Relation for user ID: 5