<?php\n$username = 'twitter';\n$xmlUrl = "http://twitter.com/users/" . $username . ".xml";\n$xml = simplexml_load_file($xmlUrl);\n\nif ($xml === false) {\n    echo "Failed loading XML: ";\n    foreach(libxml_get_errors() as $error) {\n        echo "$error\n";\n    }\n} else {\n    try {\n        echo $xml->user->profile_image_url;\n    } catch (Exception $e) {\n        echo 'Caught exception: ',  $e->getMessage(), "\n";\n    }\n}\n?>