function addFileVersion($idFile, $versionNumber, $filePath, $isFinal) {\n    global $pdo; // Assume PDO connection is established\n\n    try {\n        $stmt = $pdo->prepare("\n            INSERT INTO Versions (id_file, version_number, path_to_file, is_final)\n            VALUES (:idFile, :versionNumber, :filePath, :isFinal)\n        ");\n        $stmt->execute([\n            ':idFile' => $idFile,\n            ':versionNumber' => $versionNumber,\n            ':filePath' => $filePath,\n            ':isFinal' => $isFinal ? 1 : 0\n        ]);\n        return true;\n    } catch (PDOException $e) {\n        echo "Error: " . $e->getMessage();\n        return false;\n    }\n}