<?php\n$ids = "1 3 5 7 9";  // Your space-separated list of ids\n$idArray = explode(' ', $ids);  // Convert the string to an array of ids\n\n// Join the array into a comma-separated string for SQL IN clause\n$idList = implode(',', $idArray);\n\n$seenBeforeValue = 1;  // The value you want to set in the seen_before column\n\n$query = "UPDATE tablename SET seen_before = $seenBeforeValue WHERE id IN ($idList)";\n\n// Execute the query using your database connection, e.g., with mysqli or PDO\n// Example with mysqli:\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\nif ($conn->query($query) === TRUE) {\n    echo "Records updated successfully";\n} else {\n    echo "Error updating record: " . $conn->error;\n}\n\n$conn->close();\n?>