while (!feof($handle)) {\n    $progress = fread($handle, 8192);\n    \n    // Use preg_match to extract the percentage and filesize\n    if (preg_match('/([0-9]+(\.[0-9]+)?)% of ([0-9]+\.[0-9]+)([KMGT]?)B/', $progress, $matches)) {\n        $percentage = $matches[1];\n        $filesize = $matches[3];\n        $fileUnit = $matches[4];\n\n        // Output the extracted values\n        echo "Percentage: {$percentage}%\n";\n        echo "Filesize: {$filesize}{$fileUnit}B\n";\n    }\n}