<?php\n// Assuming $pdo is your PDO connection object\n$mainQuery = "SELECT product_id, product_type, other_fields FROM products WHERE conditions...";\n$subQuery = "SELECT DISTINCT product_type FROM ($mainQuery) AS temp";\n\n$stmt = $pdo->prepare($subQuery);\n$stmt->execute();\n$productTypes = $stmt->fetchAll(PDO::FETCH_COLUMN);\n\n// Now you can use $productTypes to populate your dropdown\nforeach ($productTypes as $type) {\n    echo "<option value=\"$type\">$type</option>";\n}\n?>