<?php\n// Assuming you have already established a connection to your database using PDO or MySQLi.\n$monsterID = $_GET['monsterid'];\n\n// Validate and sanitize the input\nif (is_numeric($monsterID)) {\n    // Use prepared statements to prevent SQL injection\n    $stmt = $pdo->prepare("SELECT 1 FROM enemies WHERE monsterid = :monsterid");\n    $stmt->bindParam(':monsterid', $monsterID, PDO::PARAM_INT);\n    $stmt->execute();\n\n    if ($stmt->rowCount() > 0) {\n        // MONSTER ID EXISTS\n        echo "Monster with ID $monsterID exists.";\n        // Run your PHP code here for when the monster id exists\n    } else {\n        // MONSTER ID DOES NOT EXIST\n        echo "Monster with ID $monsterID does not exist.";\n        // Run your PHP code here for when the monster id does not exist\n    }\n} else {\n    // Handle invalid input\n    echo "Invalid Monster ID provided.";\n}\n?>