<?php\nrequire 'vendor/autoload.php'; // Adjust path as necessary\n\nuse PHPMailer\PHPMailer\PHPMailer;\nuse PHPMailer\PHPMailer\Exception;\n\n$mail = new PHPMailer(true);\n\ntry {\n    $mail->isSMTP();\n    $mail->Host       = 'smtp.example.com';                     // Set the SMTP server to send through\n    $mail->SMTPAuth   = true;                                   // Enable SMTP authentication\n    $mail->Username   = 'your_email@example.com';               // SMTP username\n    $mail->Password   = 'your_password';                        // SMTP password\n    $mail->SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS;         // Enable TLS encryption; `PHPMailer::ENCRYPTION_SMTPS` also accepted\n    $mail->Port       = 587;                                    // TCP port to connect to\n\n    // Recipients\n    $mail->setFrom('from@example.com', 'Mailer');\n    $mail->addAddress('recipient@example.com');     // Add a recipient\n\n    // Attachments\n    $file_path = '/path/to/your/file.pdf'; // Change this to your file path\n    if (file_exists($file_path)) {\n        $mail->addAttachment($file_path);         // Add attachments\n    } else {\n        throw new Exception('File does not exist');\n    }\n\n    // Content\n    $mail->isHTML(true);\n    $mail->Subject = 'Here is the subject';\n    $mail->Body    = 'This is the HTML message body <b>in bold!</b>';\n    $mail->AltBody = 'This is the body in plain text for non-HTML mail clients';\n\n    $mail->send();\n    echo 'Message has been sent successfully';\n} catch (Exception $e) {\n    echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}";\n}\n?>