class Controller_Feed extends Controller {\n\n    public function action_rss() {\n        $response = Response::forge();\n        $response->headers('Content-Type', 'application/rss+xml');\n\n        $dom = new DOMDocument('1.0', 'UTF-8');\n        $rss = $dom->createElement('rss');\n        $dom->appendChild($rss);\n        $rss->setAttribute('version', '2.0');\n\n        $channel = $dom->createElement('channel');\n        $rss->appendChild($channel);\n\n        // Channel information\n        $channel->appendChild($dom->createElement('title', 'My RSS Feed'));\n        $channel->appendChild($dom->createElement('link', url::base()));\n        $channel->appendChild($dom->createElement('description', 'This is a description of myRSS feed'));\n\n        // Add an item\n        $item = $dom->createElement('item');\n        $channel->appendChild($item);\n        $item->appendChild($dom->createElement('title', 'News Item Title'));\n        $item->appendChild($dom->createElement('link', url::base() . 'news/123'));\n        $item->appendChild($dom->createElement('description', 'Description of the news item'));\n\n        // Output the RSS feed\n        echo $dom->saveXML();\n        return $response;\n    }\n}