<?php\nif (!empty($_POST)) {\n    $username = $_POST['username'];\n    $passwd = password_hash($_POST['passwd'], PASSWORD_BCRYPT); // Hash the password\n\n    try {\n        // Create a new PDO instance\n        $pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        // Prepare and execute an INSERT statement with bound parameters\n        $stmt = $pdo->prepare("INSERT INTO users (username, password) VALUES (:username, :passwd)");\n        $stmt->execute(['username' => $username, 'passwd' => $passwd]);\n\n        echo "Registration successful!";\n    } catch (PDOException $e) {\n        echo "Error: " . $e->getMessage();\n    }\n}\n?>\n\n<form method="post">\n    <p><input type="text" name="username" /></p>\n    <p><input type="password" name="passwd" /></p>\n    <p><input type="submit" value="Register me!" /></p>\n</form>