function input_lab_results_with_pdo($name, $image, $descrip) {\n    // Assuming $pdo is an instance of PDO already connected to your database\n    $query = "INSERT INTO pat_table (pat_name, pat_image, pat_descrip, pat_doctor, pat_resident, pat_create, pat_modify) VALUES (:name, :image, :descrip, :doctor, :resident, :create_date, :modify_date)";\n    \n    $stmt = $pdo->prepare($query);\n    \n    // Bind parameters\n    $stmt->bindParam(':name', $name);\n    $stmt->bindParam(':image', $image);\n    $stmt->bindParam(':descrip', $descrip);\n    $doctor = 0;\n    $resident = 0;\n    $create_date = date("Ymd");\n    $modify_date = date("Ymd");\n    \n    $stmt->bindParam(':doctor', $doctor);\n    $stmt->bindParam(':resident', $resident);\n    $stmt->bindParam(':create_date', $create_date);\n    $stmt->bindParam(':modify_date', $modify_date);\n\n    // Execute the statement\n    if ($stmt->execute()) {\n        echo "Record inserted successfully";\n    } else {\n        echo "Error inserting record: " . implode(", ", $stmt->errorInfo());\n    }\n}