function input_lab_results($name, $image, $descrip) {\n    $query = "INSERT INTO pat_table (pat_name, pat_image, pat_descrip, pat_doctor, pat_resident, pat_create, pat_modify) VALUES (?, ?, ?, ?, ?, ?, ?)";\n\n    // Assign values to variables\n    $doctor = 0;\n    $resident = 0;\n    $create_date = date("Ymd");\n    $modify_date = date("Ymd");\n\n    if($stmt = $this->conn->prepare($query)){\n        $stmt->bind_param('sssiiss', $name, $image, $descrip, $doctor, $resident, $create_date, $modify_date);\n        $stmt->execute();\n        die(); // Consider removing this for production code, as it stops script execution\n    } else{\n        die($this->conn->error);\n    }\n}