<?php\n\n// Define a mapping of page names to file paths.\n$routes = [\n    'shownews' => ['file' => 'shownews.php', 'validate' => function($value) { return ctype_digit($value); }],\n    'showuser' => ['file' => 'showuser.php', 'validate' => function($value) { return ctype_digit($value); }],\n    'about'    => ['file' => 'about.php'],\n    'help'     => ['file' => 'help.php'],\n    'login'    => ['file' => 'login.php'],\n];\n\n// Check if the required parameter is set.\nif (isset($_GET['action']) && isset($routes[$_GET['action']])) {\n    $route = $routes[$_GET['action']];\n    \n    // Validate the input if a validation function is provided.\n    if (isset($route['validate'])) {\n        if (!$route['validate']($_GET['id'])) {\n            die("Invalid input.");\n        }\n    }\n\n    // Include the required file safely.\n    include($route['file']);\n} else {\n    echo "Page not found.";\n}\n\n?>