<?php\nfunction getLastWeekDay($dayOfWeek) {\n    // Create a DateTime object for today.\n    $date = new DateTime('now');\n    \n    // Modify the datetime to the required day of last week.\n    $date->modify("last " . $dayOfWeek);\n    \n    return $date;\n}\n\n// Example usage:\n$lastWeeksTuesday = getLastWeekDay('tuesday')->format('F j, Y');\necho "Last week's Tuesday was: " . $lastWeeksTuesday;\n?>