<?php\n\nrequire_once 'vendor/autoload.php';\n\nuse PhpGettext\Po2Mo;\n\n// Path to your .po file\n$poFile = __DIR__ . '/example.po';\n// Path where the .mo file will be saved\n$moFile = __DIR__ . '/example.mo';\n\n// Create a Po2Mo instance and convert the file\n$converter = new Po2Mo();\n$success = $converter->convert($poFile, $moFile);\n\nif ($success) {\n    echo "Conversion successful. .mo file created at: " . $moFile;\n} else {\n    echo "Conversion failed.";\n}