// Load the Ion_auth model\n$this->load->library('ion_auth');\n\n// Check if user is logged in and has a specific role (e.g., 'admin')\nif (!$this->ion_auth->logged_in() || !$this->ion_auth->in_group('admin')) {\n    // Redirect to login or show error message\n    redirect('auth/login', 'refresh');\n}\n\n// If the user is authenticated and authorized, proceed with your controller logic