// Define your additional paths\n$otherPaths = '/path/to/your/library1:/path/to/your/library2';\n\n// Get current include path\n$currentIncludePath = ini_get('include_path');\n\n// Append new paths, ensuring they are properly separated by the system's directory separator\n$newIncludePath = $currentIncludePath . PATH_SEPARATOR . $otherPaths;\n\n// Set the new include path\nini_set('include_path', $newIncludePath);