// Initialize a cURL session\n$ch = curl_init();\n\n// Set options for the session\ncurl_setopt($ch, CURLOPT_URL, "http://your-solr-server/solr/your-collection/update");\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURL_HTTP_VERSION_1_1, true);\ncurl_setopt($ch, CURLOPT_FORBID_REUSE, false);\n\n// Loop to make multiple requests\nfor ($i = 0; $i < 100; $i++) {\n    $data = json_encode(["add" => ["doc" => ["id" => "doc_id_" . $i]]]);\n    \n    curl_setopt($ch, CURLOPT_POSTFIELDS, $data);\n    curl_setopt($ch, CURLOPT_POST, true);\n    curl_setopt($ch, CURLOPT_HTTPHEADER, array('Content-Type: application/json; charset=utf-8'));\n    \n    // Execute the request\n    $response = curl_exec($ch);\n    \n    // Check for errors and handle response as needed\n}\n\n// Close the cURL session after all requests are complete\ncurl_close($ch);