$currentYear = date('Y');\n$cmpFunction = function($a, $b) use ($currentYear) {\n    // Extract the month and day from each date\n    list($yearA, $monthA, $dayA) = explode("-", $a);\n    list($yearB, $monthB, $dayB) = explode("-", $b);\n\n    // Create DateTime objects for the current year's dates\n    $dateA = new DateTime("$currentYear-$monthA-$dayA");\n    $dateB = new DateTime("$currentYear-$monthB-$dayB");\n\n    // If the date has already passed this year, consider it for next year\n    if ($dateA < new DateTime()) {\n        $dateA->modify('+1 year');\n    }\n    if ($dateB < new DateTime()) {\n        $dateB->modify('+1 year');\n    }\n\n    // Compare the two dates\n    return $dateA <=> $dateB;\n};\n\n$a = ["1980-04-14", "2007-06-08", "2008-12-25", "1978-11-03"];\nusort($a, $cmpFunction);\n\nprint_r($a);