function displayTXTList($fileName) {\n    ini_set('auto_detect_line_endings', true); // Enable auto detection of line endings\n    if(file_exists($fileName)) {\n        $file = fopen($fileName, 'r');\n        while(($name = fgets($file)) !== false) { \n            echo('<tr><td align="center">' . htmlspecialchars(trim($name)) . '</td></tr>');\n        }\n        fclose($file);\n    } else {\n        echo('<tr><td align="center">placeholder</td></tr>');\n    }       \n}