$dbconn = pg_connect("host=localhost port=5432 dbname=masi user=masi password=123");\n$randomNumber = rand();\n$queryName = "query" . $randomNumber;\n\ntry {\n    $result = pg_prepare($dbconn, $queryName, "SELECT passhash_md5 FROM users WHERE email=$1;");\n    if (!$result) {\n        throw new Exception('Prepared statement failed.');\n    }\n    $passhash_md5 = pg_execute($dbconn, $queryName, array($_POST['email']));\n} catch (Exception $e) {\n    echo $e->getMessage();\n}