<?php\n// Merge JS files\n$jsFiles = glob('path/to/js/*.js');\n$mergedJs = '';\n\nforeach ($jsFiles as $file) {\n    $mergedJs .= file_get_contents($file);\n}\n\n// Optionally minify the merged JS (you can use a library for this)\n// For example, using Minify_Javascript:\n// require_once 'Minify/JS.php';\n// $minifiedJs = Minify_JS::pack($mergedJs);\n\n$finalJsContent = $mergedJs; // Use $minifiedJs if minifying\n\nfile_put_contents('js/all.js', $finalJsContent);\n\n\n// Merge CSS files\n$cssFiles = glob('path/to/css/*.css');\n$mergedCss = '';\n\nforeach ($cssFiles as $file) {\n    $mergedCss .= file_get_contents($file);\n}\n\n// Optionally minify the merged CSS (you can use a library for this)\n// For example, using Minify_CSS:\n// require_once 'Minify/CSS.php';\n// $minifiedCss = Minify_CSS::minify($mergedCss);\n\n$finalCssContent = $mergedCss; // Use $minifiedCss if minifying\n\nfile_put_contents('css/all.css', $finalCssContent);