function cleanString($string) {\n    $cleaned = '';\n    for ($i = 0; $i < strlen($string); $i++) {\n        $char = $string[$i];\n        $ordValue = ord($char);\n        \n        // Assuming you want to keep printable ASCII characters and filter out others\n        if (($ordValue >= 32 && $ordValue <= 126) || $ordValue == 9 || $ordValue == 10 || $ordValue == 13) {\n            // Printable chars, tab, LF, CR\n            $cleaned .= $char;\n        } else {\n            echo "Removed character with ordinal value: " . $ordValue . "\n";\n        }\n    }\n    return $cleaned;\n}\n\n$string = getStringFromDatabase();\n$cleanString = cleanString($string);\nprintf("%s\n", $cleanString);\n\n// Optionally, parse XML if needed\n$xml = simplexml_load_string($cleanString);\nif ($xml === false) {\n    echo "Failed loading XML";\n} else {\n    print_r($xml);\n}