$pdo = new PDO('mysql:host=localhost;dbname=testdb', 'username', 'password');\n$pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n$data = "It's a \"test\" string.\nWith new line.";\n\n// Prepare and execute the statement\n$stmt = $pdo->prepare("INSERT INTO test_table (data) VALUES (:data)");\n$stmt->execute([':data' => $data]);\n\n// Retrieve\n$query = "SELECT data FROM test_table WHERE id = :id";\n$stmt = $pdo->prepare($query);\n$stmt->execute([':id' => 1]); // Assuming you have an ID to filter by\n$unescapedData = $stmt->fetchColumn();\n\necho "Unescaped Data: $unescapedData";