function removeSpecificGetVariable($url, $keyToRemove) {\n    $parsedUrl = parse_url($url);\n    parse_str($parsedUrl['query'], $queryParams);\n\n    if (isset($queryParams[$keyToRemove])) {\n        unset($queryParams[$keyToRemove]);\n        $queryString = http_build_query($queryParams);\n        $parsedUrl['query'] = $queryString;\n    }\n\n    return http_build_url($parsedUrl);\n}\n\n// Standard PHP approach without pecl_http extension\nfunction removeSpecificGetVariableStandard($url, $keyToRemove) {\n    $parsedUrl = parse_url($url);\n    parse_str($parsedUrl['query'], $queryParams);\n\n    if (isset($queryParams[$keyToRemove])) {\n        unset($queryParams[$keyToRemove]);\n    }\n\n    return $parsedUrl['scheme'] . '://' . $parsedUrl['host'] . (isset($parsedUrl['port']) ? ':' . $parsedUrl['port'] : '') . $parsedUrl['path'] . '?' . http_build_query($queryParams);\n}\n\n$current_url = "https://example.com/page.php?foo=bar&baz=qux";\necho removeSpecificGetVariableStandard($current_url, 'foo'); // Outputs: https://example.com/page.php?baz=qux