function cleanCurrencyString($value) {\n    // Remove dollar sign and commas\n    $cleanedValue = str_replace(['$', ','], '', $value);\n    // Convert to float\n    return (float)$cleanedValue;\n}\n\n$string1 = "$50,000";\n$string2 = "$50000";\n$string3 = "50,000";\n\n$total = cleanCurrencyString($string1) + cleanCurrencyString($string2) + cleanCurrencyString($string3);\necho $total; // This will correctly output 150000