$array = [\n    // Your array data here...\n];\n\n// Custom comparison function for sorting\nusort($array, function($a, $b) {\n    // If $a is the parent of $b\n    if ($a['id'] == $b['parent']) {\n        return -1; // Place $b after $a\n    }\n    // If $b is the parent of $a\n    if ($b['id'] == $a['parent']) {\n        return 1; // Place $a after $b\n    }\n\n    // Ensure that siblings are sorted by their original order or another criterion\n    if ($a['depth'] == $b['depth'] && $a['parent'] == $b['parent']) {\n        return $a['id'] <=> $b['id']; // Sort siblings by id, for example\n    }\n\n    // Otherwise, sort by depth to maintain hierarchy\n    return $a['depth'] <=> $b['depth'];\n});\n\n// Print the sorted array\nprint_r($array);