protected function _initRoutes() {\n    $frontController = Zend_Controller_Front::getInstance();\n    $router = $frontController->getRouter();\n\n    // Add WML route\n    $wmlRoute = new Zend_Controller_Router_Route(\n        ':format/:controller/:action/*',\n        array('module' => 'default', 'format' => 'wml')\n    );\n    $router->addRoute('wmlRoute', $wmlRoute);\n\n    // Add XHTML route\n    $xhtmlRoute = new Zend_Controller_Router_Route(\n        ':format/:controller/:action/*',\n        array('module' => 'default', 'format' => 'xhtml')\n    );\n    $router->addRoute('xhtmlRoute', $xhtmlRoute);\n}