$dbconn = pg_connect("host=localhost port=5432 dbname=masi user=masi password=123");\n$queryName = "query11";\n\ntry {\n    $result = pg_prepare($dbconn, $queryName, "SELECT passhash_md5 FROM users WHERE email=$1;");\n    if (!$result) {\n        throw new Exception(pg_last_error($dbconn));\n    }\n    $passhash_md5 = pg_execute($dbconn, $queryName, array($_POST['email']));\n} catch (Exception $e) {\n    // Check if the error is due to the statement already existing\n    if (strpos($e->getMessage(), 'prepared statement "query11" already exists') !== false) {\n        // Prepare and execute again with a different name or re-use the existing one\n        $passhash_md5 = pg_execute($dbconn, $queryName, array($_POST['email']));\n    } else {\n        echo $e->getMessage();\n    }\n}