<?php\n\n// ID of the phpBB group that should have access\n$required_group_id = 2;\n\nif ( $userdata['session_logged_in'] ) {\n    // Fetch user's groups\n    $sql = "SELECT g.group_id FROM " . USER_GROUP_TABLE . " ug, " . GROUPS_TABLE . " g WHERE ug.user_pending = 0 AND ug.group_id = g.group_id AND ug.user_id = " . intval($userdata['user_id']);\n\n    if ( !($result = $db->sql_query($sql)) ) {\n        message_die(GENERAL_ERROR, 'Could not query group data', '', __LINE__, __FILE__, $sql);\n    }\n\n    $group_array = array();\n    while ($row = $db->sql_fetchrow($result)) {\n        $group_array[] = $row['group_id'];\n    }\n    $db->sql_freeresult($result);\n\n    // Check if user is in the required group\n    if (in_array($required_group_id, $group_array)) {\n        echo "Welcome, " . htmlspecialchars($userdata['username']) . "! You are a member of the required group.";\n        // Add your additional content here\n    } else {\n        echo "You do not have permission to view this page.";\n        // Optionally provide redirect or login information\n    }\n} else {\n    echo "Please log in to access this section.";\n    // Optionally provide a link to the phpBB login page\n}\n?>