<?php\nrequire "vendor/autoload.php";\nuse Abraham\TwitterOAuth\TwitterOAuth;\n\nsession_start();\n\ndefine('CONSUMER_KEY', 'your-consumer-key');\ndefine('CONSUMER_SECRET', 'your-consumer-secret');\n\nif (!isset($_SESSION['access_token'])) {\n    $connection = new TwitterOAuth(CONSUMER_KEY, CONSUMER_SECRET);\n    $request_token = $connection->oauth('oauth/request_token', array('oauth_callback' => "http://yourwebsite.com/callback.php"));\n\n    $_SESSION['oauth_token'] = $request_token['oauth_token'];\n    $_SESSION['oauth_token_secret'] = $request_token['oauth_token_secret'];\n\n    // Redirect the user to Twitter's authorize page\n    $url = $connection->url('oauth/authorize', array('oauth_token' => $request_token['oauth_token']));\n    header('Location: ' . $url);\n} else {\n    // Access token and secret already exist, use it for API calls\n    $access_token = $_SESSION['access_token'];\n\n    $connection = new TwitterOAuth(CONSUMER_KEY, CONSUMER_SECRET, $access_token['oauth_token'], $access_token['oauth_token_secret']);\n    \n    // Example: Get the user's tweets\n    $tweets = $connection->get("statuses/user_timeline", ["count" => 5]);\n    print_r($tweets);\n}\n?>