class User {\n    private $name;\n    private $email;\n\n    // Constructor method to initialize the user object\n    public function __construct($name, $email) {\n        $this->name = $name;\n        $this->email = $email;\n    }\n\n    // Method to get the user's name\n    public function getName() {\n        return $this->name;\n    }\n\n    // Method to set a new email for the user\n    public function setEmail($newEmail) {\n        $this->email = $newEmail;\n    }\n}