function isValidYoutubeEmbed($embedCode) {\n    // Regex pattern for validating YouTube embed iframe\n    $pattern = '/<iframe\s+src=["\']https:\/\/www\.youtube\.com\/embed\/[a-zA-Z0-9_-]{11}["\'].*?><\/iframe>/';\n    \n    return preg_match($pattern, $embedCode) === 1;\n}\n\n// Example usage\n$embedCode = '<iframe src="https://www.youtube.com/embed/dQw4w9WgXcQ" width="560" height="315"></iframe>';\nif (isValidYoutubeEmbed($embedCode)) {\n    echo "Valid YouTube embed code.";\n} else {\n    echo "Invalid YouTube embed code.";\n}