<?php\nsession_start();\n$zipinfo = isset($_SESSION['zip_code']) ? $_SESSION['zip_code'] : '';\n\nif (!$zipinfo) {\n    echo "[]"; // Return an empty array if no zip code is found\n    exit;\n}\n\n$conn = new mysqli('localhost', 'root', '', 'post_db');\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$stmt = $conn->prepare("SELECT p_o_box FROM p_o_box WHERE zip_code=?");\n$stmt->bind_param("s", $zipinfo);\n$stmt->execute();\n$result = $stmt->get_result();\n\n$a = [];\nwhile ($row = $result->fetch_assoc()) {\n    $a[] = $row['p_o_box'];\n}\n\necho json_encode($a); // Return the array as JSON\n$conn->close();\n?>