$doc = new DomDocument();\n$doc->loadXML(file_get_contents("php://input"));\n$profile_node = $doc->getElementsByTagName("profile")->item(0);\n\nif ($profile_node !== null) {\n    // Accessing the childNodes property\n    foreach ($profile_node->childNodes as $node) {\n        // Check if the node is an element to avoid processing text nodes, etc.\n        if ($node->nodeType === XML_ELEMENT_NODE) {\n            echo "Node name: " . $node->nodeName . "\n";\n            // Do something with this node\n        }\n    }\n} else {\n    echo "No profile node found.\n";\n}