<?php\n$fb_uids = [123456, 789012, 345678]; // Example array of Facebook user IDs\n\n// Prepare the placeholders for IN() clause\n$placeholders = implode(',', array_fill(0, count($fb_uids), '?'));\n\n// Create a prepared statement\n$stmt = $pdo->prepare("SELECT username FROM users WHERE fb_uid IN ($placeholders)");\n\n// Execute the query with the array of IDs\n$stmt->execute($fb_uids);\n\n// Fetch results\n$usernames = $stmt->fetchAll(PDO::FETCH_COLUMN);\n\n// Output the usernames\nprint_r($usernames);\n?>