<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $resolutionData = json_decode(file_get_contents('php://input'), true);\n    \n    // Validate and sanitize the input\n    if (isset($resolutionData['width']) && isset($resolutionData['height'])) {\n        $width = filter_var($resolutionData['width'], FILTER_SANITIZE_NUMBER_INT);\n        $height = filter_var($resolutionData['height'], FILTER_SANITIZE_NUMBER_INT);\n\n        // Now you can use $width and $height\n        echo "Received Resolution: {$width}x{$height}";\n    } else {\n        http_response_code(400); // Bad Request\n        echo "Invalid data provided";\n    }\n}\n?>