$letters = range('A', 'Z');\necho '<em>Jump to:</em><ul class="letters">';\n// anchors\nforeach ($letters as $letter) {            \n    echo '<li><a href="#botany-words-' . strtolower($letter) . '">' . $letter . '</a></li>';      \n}        \necho '</ul>';        \n// start listing them!        \n$currentLetter = null;\necho '<dl id="botany-words">';\n\nforeach ($content as $botanyWord => $definition) {\n    $firstLetter = strtoupper(substr($botanyWord, 0, 1));\n\n    if ($currentLetter !== $firstLetter && in_array($firstLetter, $letters)) {\n        if ($currentLetter !== null) {\n            echo '</dl>'; // Close the previous <dl> before starting a new one\n        }\n        echo '<h3 id="botany-words-' . strtolower($firstLetter) . '">' . $firstLetter . '</h3>';\n        echo '<dl id="botany-words-' . strtolower($firstLetter) . '">'; // Start a new <dl>\n        $currentLetter = $firstLetter;\n    }\n\n    echo '<dt>' . htmlspecialchars($botanyWord) . '</dt>';\n    echo '<dd>' . htmlspecialchars($definition) . '</dd>';\n}\necho '</dl>'; // Close the last <dl>