class Dog_Exception {\n    public function __toString() {\n        // Optionally add functionality if you want to convert Exception to string\n        return "No dog seen";\n    }\n\n    public function isSuccessful() {\n        return false;\n    }\n}\n\nclass Dog {\n    public function lookAround()\n    {\n        $result = $this->seeDog();\n        if ($result->isSuccessful()) {\n            return "bark"; // or some other true-like result\n        } else {\n            return "sit"; // or some other false-like result\n        }\n    }\n\n    public function seeDog()\n    {\n        if (/* condition to see a dog */) {\n             $dog = new stdClass();\n             $dog->isSuccessful = function() { return true; };\n             return $dog;\n        } else {\n            return new Dog_Exception();\n        }\n    }\n}