// Function to escape special characters in a string for MS SQL Server\nfunction sql_escape_string($string) {\n    return str_replace("'", "''", $string);\n}\n\n// Example usage\n$userId = sql_escape_string($_POST['user_id']);\n$userName = sql_escape_string($_POST['user_name']);\n\n$query = "INSERT INTO users (id, name) VALUES ('$userId', '$userName')";\n\n$result = mssql_query($query);\n\nif (!$result) {\n    die('Invalid query: ' . mssql_get_last_message());\n}